import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CreateEmployee from '../forms/CreateEmployee';
import { FaPlus, FaEdit, FaTrashAlt } from "react-icons/fa";
import Search from '../components/Search';

const EmployeeList = () => {
    const [employees, setEmployees] = useState([]);
    const [modalIsOpen, setModalIsOpen] = useState(false);

    useEffect(() => {
        fetchEmployees();
    }, []);

    const fetchEmployees = async () => {
        try {
            const response = await axios.get('http://localhost:8000/employee');
            console.log(response.data); // Log to see the actual data structure
            setEmployees(response.data);
            console.log(employee.imageURL);

        } catch (error) {
            console.error("Error fetching employees:", error);
        }
    };

    const handleEmployeeCreated = () => {
        fetchEmployees();
        setModalIsOpen(false);
    };

    const handleEdit = (id) => {
        console.log("Edit:", id);
    };

    const handleDelete = (id) => {
        console.log("Delete:", id);
    };

    return (
        <div>
            <button onClick={() => setModalIsOpen(true)} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full shadow-lg">
                <FaPlus />
            </button>
            {modalIsOpen && <CreateEmployee closeModal={() => setModalIsOpen(false)} onEmployeeCreated={handleEmployeeCreated} />}
            <div className='relative py-4 ml-[84%]'>
                <Search />
            </div>
            <table className="min-w-full table-auto">
                <thead className="bg-gray-800 text-white">
                    <tr>
                        <th className="px-4 py-2">ID</th>
                        <th className="px-4 py-2">Image</th>
                        <th className="px-4 py-2">Name</th>
                        <th className="px-4 py-2">Email</th>
                        <th className="px-4 py-2">Mobile No</th>
                        <th className="px-4 py-2">Designation</th>
                        <th className="px-4 py-2">Gender</th>
                        <th className="px-4 py-2">Courses</th>
                        <th className="px-4 py-2">Create Date</th>
                        <th className="px-4 py-2">Action</th>
                    </tr>
                </thead>
                <tbody>
                {employees.map((employee, index) => (
                    <tr key={employee.id}>
                        <td className="border px-4 py-2">{employee.id} ({employees.length})</td>
                        <td className="border px-4 py-2">
                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                <img src={employee.imageURL} alt={`${employee.name}'s profile`} style={{ width: '50px', height: '50px', borderRadius: '50%' }} />
                                <span style={{ marginLeft: '10px' }}>{employee.imageFilename}</span>
                            </div>
                        </td>
                        <td className="border px-4 py-2">{employee.name}</td>
                        <td className="border px-4 py-2">{employee.email}</td>
                        <td className="border px-4 py-2">{employee.mob_no}</td> 
                        <td className="border px-4 py-2">{employee.designation}</td>
                        <td className="border px-4 py-2">{employee.gender}</td>
                        <td className="border px-4 py-2">{employee.courses.join(", ")}</td> 
                        <td className="border px-4 py-2">{employee.created_date}</td>
                        <td className="border px-4 py-2 flex justify-around">
                            <button onClick={() => handleEdit(employee.id)} className="text-green-500 hover:text-green-700">
                                <FaEdit />
                            </button>
                            <button onClick={() => handleDelete(employee.id)} className="text-red-500 hover:text-red-700">
                                <FaTrashAlt />
                            </button>
                        </td>
                    </tr>
                ))}

                </tbody>

            </table>
        </div>
    );
};

export default EmployeeList;
