import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const SignIn = ({ toggleForms, loginSuccess }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/register', {
        name,
        email,
        password
      });

      if (response.data.success) {
        console.log('Sign up success:', response.data);
        toast.success('Sign up successful!');
        loginSuccess();
        navigate('/'); // Redirect on successful sign up
      } else {
        toast.error(response.data.message || 'Sign up failed, please try again.');
      }
    } catch (error) {
      console.error('Sign up error:', error.response || error.message);
      if (error.response) {
        // Handling different types of response structures
        const message = error.response.data.message || error.response.data.error || "An error occurred during sign up.";
        toast.error(message);
      } else {
        // For network errors or other issues not related to HTTP responses
        toast.error(error.message || "Network error.");
      }
    }
  };

  return (
    <div className='Login'>
      <ToastContainer />
      <form className='form' onSubmit={handleSubmit}>
        <div className="form-elements">
          <label htmlFor="name">Name</label>
          <input
            className='form-inputs'
            type="text"
            placeholder='Enter your Name'
            id="name"
            value={name}
            onChange={handleNameChange}
          />
        </div>
        <div className="form-elements">
          <label htmlFor="email">Email</label>
          <input
            className='form-inputs'
            type="email"
            placeholder='Enter your Email'
            id="email"
            value={email}
            onChange={handleEmailChange}
          />
        </div>
        <div className="form-elements">
          <label htmlFor="password">Password</label>
          <input
            className='form-inputs'
            type="password"
            placeholder='Enter your Password'
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>
        <div className="form-btn">
          <button type="submit" className='form-btn-inputs'>Sign In</button>
          <button type="button" onClick={toggleForms}>Already have an account? Login</button>
        </div>
      </form>
    </div>
  );
}

export default SignIn;
