import React, { useState } from 'react';
import axios from 'axios';

const SignIn = ({ toggleForms }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/login', {
        name: name,
        email: email,
        password: password
      });
      console.log('Registration success:', response.data);
      // Here you can handle redirections or further actions after successful registration
    } catch (error) {
      console.error('Registration error:', error.response || error.message);
      // Handle errors here, such as validation or server errors
    }
  };

  return (
    <div className='Login'>
        <form className='form' onSubmit={handleSubmit}>
            <div className="form-elements">
                <label htmlFor="name">Name</label>
                <input
                  className='form-inputs'
                  type="text"
                  placeholder='Enter your Name'
                  id="name"
                  value={name}
                  onChange={handleNameChange}
                />
            </div>
            <div className="form-elements">
                <label htmlFor="email">Email</label>
                <input
                  className='form-inputs'
                  type="email"
                  placeholder='Enter your Email'
                  id="email"
                  value={email}
                  onChange={handleEmailChange}
                />
            </div>
            <div className="form-elements">
                <label htmlFor="password">Password</label>
                <input
                  className='form-inputs'
                  type="password"
                  placeholder='Enter your Password'
                  id="password"
                  value={password}
                  onChange={handlePasswordChange}
                />
            </div>
            <div className="form-btn">
                <button type="submit" className='form-btn-inputs'>Sign In</button>
                <button type="button" onClick={toggleForms}>Already have an account? Login</button>
            </div>
        </form>
    </div>
  );
}

export default SignIn;
