import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


const CreateEmployee = ({ closeModal, onEmployeeCreated }) => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        mob_no: '',
        designation: '',
        gender: '',
        courses: [],
        img: null
    });

    const navigate = useNavigate(); // Use the useNavigate hook

    const handleInputChange = (event) => {
        const { name, value, type, checked, files } = event.target;
        if (type === "checkbox") {
            setFormData(prev => ({
                ...prev,
                courses: checked ? [...prev.courses, value] : prev.courses.filter(c => c !== value)
            }));
        } else if (type === "file") {
            setFormData(prev => ({
                ...prev,
                img: files[0]
            }));
        } else if (type === "radio") {
            setFormData(prev => ({
                ...prev,
                gender: value
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const data = new FormData();
        Object.keys(formData).forEach(key => {
            if (key === "courses") {
                formData[key].forEach(course => data.append(key, course));
            } else {
                data.append(key, formData[key]);
            }
        });

        try {
            const response = await axios.post('http://localhost:8000/createEmployee', data, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            if (response.status === 200) {
                alert('Employee created successfully!');
                onEmployeeCreated();
                closeModal();
                navigate('/employee_list'); // Redirect to employee list
            }
        } catch (error) {
            if (error.response) {
                // Handle different error statuses
                if (error.response.status === 409) {
                    alert('Employee already exists in the database.');
                } else {
                    alert('Failed to create employee. Please try again.');
                }
            } else {
                console.error('Failed to create employee:', error);
                alert('Network error or server is down.');
            }
        }
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="modal-close" onClick={closeModal}>&times;</span>
                <form onSubmit={handleSubmit} className='create-employee-form'>
                    {/* Form content */}
                </form>
            </div>
        </div>
    );
};

export default CreateEmployee;
