import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ toggleForms, loginSuccess }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:8000/login', {
        email,
        password
      });

      console.log('Response:', response.data);  // Debug to see what the server sends back

      if (response.data.success) {
        console.log('Login success:', response.data);
        loginSuccess();  // Update login state context or similar
        navigate('/dashboard');  // Adjust the redirect as necessary
      } else {
        console.error('Login failed:', response.data.message);
        setError('Invalid email or password');  // Show a user-friendly error message
      }
    } catch (error) {
      console.error('Login error:', error.response ? error.response.data : error.message);
      setError('Failed to login due to network or server error.');  // Generic error if server communication fails
    }
};


  return (
    <div className='Login'>
        <form className='form' onSubmit={handleSubmit}>
            <div className="form-elements">
                <label htmlFor="email">Email</label>
                <input
                  className='form-inputs'
                  type="email"
                  placeholder='Enter your Email'
                  id="email"
                  value={email}
                  onChange={handleEmailChange}
                />
            </div>
            <div className="form-elements">
                <label htmlFor="password">Password</label>
                <input
                  className='form-inputs'
                  type="password"
                  placeholder='Enter your Password'
                  id="password"
                  value={password}
                  onChange={handlePasswordChange}
                />
            </div>
            {error && <div style={{ color: 'red' }}>{error}</div>}
            <div className="form-btn">
                <button type="submit" className='form-btn-inputs'>Login</button>
                <button type="button" onClick={toggleForms}>Need an account? Sign Up</button>
            </div>
        </form>
    </div>
  );
}

export default Login;
